
public class doStructure_somandoNumeros {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
        do {
            ---
            --- instruções
            ---
        }
        while (condição);

        * Entre as {---} a parte do código a ser repetida */
	    // #    Exemplo 1   #
	    // Estrutura de Repetição Exemplo 2
	    int soma = 0;
	    int x = 1;
	    do{
	        soma=soma+x;
	        x=x+1;
	    }
	    while (x <= 10);
	    System.out.println(" Soma = "+soma);
	}

}
