
public class whileStructure_somandoNumeros {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// #    Exemplo 2   #
	    //Faça um programa para somar de 1 a 10 e mostrar o resultado desta soma.
	    // Estrutura de Repetição Exemplo 2
	    int soma = 0;
	    int x = 1;
	    while (x <= 10){
	        soma=soma+x;
	        x = x + 1;
	    }
	    System.out.println(" Soma = "+soma);
	}

}
