
public class SomarRepeticaoes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Estrutura de Repetição Exemplo 2 
	      int soma = 0; 
	      int x = 1;
	      
	      while (x<=10) {    
	          soma = soma + x; 
	          x = x + 1;
	      }
	      
	      System.out.println(" Soma = "+soma); 
	}

}
